mapper.update:
update member set
M_PW= #{M_PW}
where
M_ID=#{M_ID}

update member set
M_NAME=#{M_NAME}, M_EMAIL=#{M_EMAIL}, M_PHONE=#{M_PHONE} where
M_ID=#{M_ID}

update member set
M_CLASS=4, DELETE_CON=#{DELETE_CON}, DELETE_TIME=sysdate where
M_ID=#{M_ID}

update member set
M_LASTLOGIN = sysdate where
M_ID=#{M_ID}
